import React, { useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const Logo = styled(Link)`
  font-family: "POKEMON FONT";
  color: #ffcc07 !important;
  text-shadow: -3px -3px 0 #3069ab, 3px -3px 0 #3069ab, -3px 3px 0 #3069ab,
    3px 3px 0 #3069ab;
  transform: rotate(-10deg);
  margin-bottom: 12px;
  max-width: 125px;
`;

const TopNav = (props) => {
  const [isNavCollapsed, setIsNavCollapsed] = useState(true);

  const handleNavCollapse = () => setIsNavCollapsed(!isNavCollapsed);

  return (
    <nav className="navbar navbar-expand-md navbar-dark bg-dark fixed-top">
      <Logo
        to={"/"}
        className="navbar-brand col-sm-3 col-md-2 mr-0 align-items-center"
      >
        Pokedex
      </Logo>{" "}
      <button
        class="custom-toggler navbar-toggler"
        type="button"
        data-toggle="collapse"
        data-target="#navbarsExample09"
        aria-controls="navbarsExample09"
        aria-expanded={!isNavCollapsed ? true : false}
        aria-label="Toggle navigation"
        onClick={handleNavCollapse}
      >
        <span class="navbar-toggler-icon"></span>
      </button>
      <div
        class={`${isNavCollapsed ? "collapse" : ""} navbar-collapse`}
        id="navbarsExample09"
      >
        <Link className="nav-link text-info" to={"/gen1"}>
          Gen1
        </Link>
        <Link className="nav-link text-info" to={"/gen2"}>
          Gen2
        </Link>
        <Link className="nav-link text-info" to={"/gen3"}>
          Gen3
        </Link>
        <Link className="nav-link text-info" to={"/gen4"}>
          Gen4
        </Link>
        <Link className="nav-link text-info" to={"/gen5"}>
          Gen5
        </Link>
        <Link className="nav-link text-info" to={"/gen6"}>
          Gen6
        </Link>
        <Link className="nav-link text-info" to={"/gen7"}>
          Gen7
        </Link>
      </div>
    </nav>
  );
};

export default TopNav;
